import os
import glob
import pandas as pd
import argparse
import xml.etree.ElementTree as ET

DATASET_PATH = "D:/delaunay/Maquet/dataset/"

T = "train"  # 'test' or 'train'
INPUT_DIR = DATASET_PATH + "images/" + T
OUTPUT_FILE = DATASET_PATH + "/annotations/" + T + "_labels.csv"


def xml_to_csv(path):
    # Iterates through .xml files (generated by labelImg) in a directory and combines them in a Pandas datagrame.

    xml_list = []
    for xml_file in glob.glob(path + '/*.xml'):
        tree = ET.parse(xml_file)
        root = tree.getroot()
        for member in root.findall('object'):
            value = (root.find('filename').text,
                     int(root.find('size')[0].text),
                     int(root.find('size')[1].text),
                     member[0].text,
                     int(member[4][0].text),
                     int(member[4][1].text),
                     int(member[4][2].text),
                     int(member[4][3].text)
                     )
            xml_list.append(value)
    column_name = ['filename', 'width', 'height',
                   'class', 'xmin', 'ymin', 'xmax', 'ymax']
    xml_df = pd.DataFrame(xml_list, columns=column_name)
    return xml_df


def main():

    xml_df = xml_to_csv(INPUT_DIR)
    xml_df.to_csv(
        OUTPUT_FILE, index=None)
    print('Successfully converted xml to csv.')


if __name__ == '__main__':
    main()
